import React, { useState } from "react";
import { UploadIcon } from "@heroicons/react/outline";
import Link from "next/link";
import {  createUserWithEmailAndPassword } from "firebase/auth";
import {auth,db} from '../firebase'
import { doc, serverTimestamp, setDoc } from "firebase/firestore"; 
import { useSelector,useDispatch } from "react-redux";
import { useRouter } from "next/router";
import FormInput from "@/components/FormInput";



import { LOGIN } from "@/features/user/userSlice";

const Signup = () => {
    const route =useRouter()
    const user =useSelector(state=>state.user.user)
    const [done,setDone]=useState(false)

    const dispatch=useDispatch()
    const [loading,setLoading]=useState(false)
  const [input, setInput] = useState({username:'',
email:'',
password:'',
confirmPassword:''
});
  
  const [fireErr, setFireErr] = useState("");

  const [date, setDate] = useState("");
  const [file, setFile] = useState(null);
  const formData = [
    {
        id:'1',
        name:'username',
        type:'text',
        placeholder:'Enter your name',
        false: 'Enter a valid name between 2-20 letters with no special characters',
        className:'input ',
        minLength:'2',
        maxLength:'20',
        required:true,
        value:input.username,
        pattern:'^[a-zA-Z]{2,15}$'
       
    },
    {
        id:'2',
        name:'email',
        type:'email',
        placeholder:'Enter your email',
        false: 'Enter a valid email',
        className:'input',
        required:true,
         value:input.email
      


    },
    {
        id:'3',
        name:'password',
        type:'password',
        placeholder:'Enter your password',
        false: 'Enter a valid password between 6 and 20 character',
        className:'input',
        minLength:'6',
        maxLength:'20',
        required:true,
         value:input.password
        
    },
    {
        id:'4',
        name:'confirmPassword',
        type:'password',
        placeholder:'Confirm  your password',
        false: 'Your passwords don,t match',
        className:'input',
      pattern:input.password,
        required:true,
         value:input.confirmPassword
        
    },
   
 
]

  const handleSignup = async (e) => {
    e.preventDefault();

 
  try{
    setLoading(true)
    const res =await createUserWithEmailAndPassword(auth, input.email, input.password)
    await setDoc(doc(db, "users", res.user.uid), {
        name: input.username,
        email:input.email,
        password:input.password,
        birthday:date || '',
        timestamp:serverTimestamp()
      });
      
      formData.map((el=>{
       
       
        
        setInput(input=>({...input,[el.name]:''}))}))
     
      dispatch(LOGIN({id:res.user.uid,
        email:res.user.email}))
        console.log(user)
        route.push('/')
        setLoading(false)
     
  }
  catch(err)
{  
    setLoading(false)
    setFireErr(err.message)
}
  
  };

  const onChange=(e,el)=> {
    setInput({ ...input, [el?.name]: e.target?.value });
            
    setFireErr(null)
  }

  return (
    <div className="h-screen flex flex-col sm:flex-row justify-start sm:justify-center items-center gap-3 sm:gap-28 p-4">
      <div className="flex  justify-center items-center  ">
        <img
          className="fully my-img-sm md:my-img"
          src={
            file
              ? URL.createObjectURL(file)
              : "data:image/png;base64,"
          }
          alt=""
        />
      </div>

      <form   onSubmit={handleSignup} className="flex flex-col gap-5 w-3/4 sm:w-1/3">
        {formData.map((el) => (
         
            <FormInput el={el} onChange={onChange} />
          
        ))}
       

        <input
          type="date"
          className="text-gray-500 outline-none cursor-pointer"
          onChange={(e) => setDate(e.target.value)}
        />

        <label className="flex gap-2 text-gray-500" htmlFor="image">
          Upload Image <UploadIcon className="w-5 h-5 cursor-pointer" />{" "}
        </label>
        <input
          className="hidden"
          id="image"
          type="file"
          name="image"
          onChange={(e) => setFile(e.target.files[0])}
        />
        {fireErr && (
          <p className="text-red-500 text-xs">Error:{fireErr}</p>
        )}

        <button
          className="text-white  p-4 cursor=pointer-border-0 online-none bg-blue-500"
        
        >
        {loading? 'Loading...' :  'Sign up'}
        </button>
        <p className="text-gray-500">
          Back to{" "}
          <Link className="underline text-blue-500" href="/login">
            Login.
          </Link>
        </p>
      </form>
    </div>
  );
};

export default Signup;
